import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getBlogMetadata } from '../data/blogData';

const Home = () => {
  const [blogPosts, setBlogPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadPosts = async () => {
      try {
        const posts = await getBlogMetadata();
        setBlogPosts(posts);
      } catch (error) {
        console.error('Error loading blog posts:', error);
      } finally {
        setLoading(false);
      }
    };

    loadPosts();
  }, []);

  if (loading) {
    return (
      <div>
        <section className="hero">
          <div className="container">
            <h1>Quantitative Investment Analysis Blog</h1>
            <p>Learn the cutting-edge algorithms and methods in quantitative investment analysis.</p>
          </div>
        </section>
        <div className="container">
          <p>Loading posts...</p>
        </div>
      </div>
    );
  }

  return (
    <div>
      {/* Hero Section */}
      <section className="hero">
        <div className="container">
          <h1>Quantitative Investment Analysis Blog</h1>
          <p>Learn the cutting-edge algorithms and methods in quantitative investment analysis.</p>
        </div>
      </section>

      {/* Strategy Cards */}
      <section className="container">
        <div className="card-grid">
          {blogPosts.map((post) => (
            <Link to={`/blog/${post.slug}`} key={post.id} className="card">
              <img 
                src={`/assets/images/${post.image}`} 
                alt={post.title}
                className="card-image"
              />
              <h3 className="card-title">{post.title}</h3>
              <p className="card-excerpt">{post.excerpt}</p>
              <div className="card-meta">
                <span>{post.category}</span>
                <span>{post.date}</span>
              </div>
            </Link>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
