import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

/**
 * Hero Section Component
 * Features dynamic typing effect and compelling call-to-action
 */
const Hero = () => {
  const navigate = useNavigate();
  const [currentRole, setCurrentRole] = useState(0);
  const [displayText, setDisplayText] = useState('');
  const [isTyping, setIsTyping] = useState(true);

  const roles = [
    'Senior Data Scientist',
    'AI Engineer', 
    'Machine Learning Expert',
    'Deep Learning Specialist',
    'NLP Engineer'
  ];

  useEffect(() => {
    let timeout;
    const role = roles[currentRole];
    
    if (isTyping) {
      if (displayText.length < role.length) {
        timeout = setTimeout(() => {
          setDisplayText(role.slice(0, displayText.length + 1));
        }, 100);
      } else {
        timeout = setTimeout(() => {
          setIsTyping(false);
        }, 2000);
      }
    } else {
      if (displayText.length > 0) {
        timeout = setTimeout(() => {
          setDisplayText(displayText.slice(0, -1));
        }, 50);
      } else {
        setCurrentRole((prev) => (prev + 1) % roles.length);
        setIsTyping(true);
      }
    }

    return () => clearTimeout(timeout);
  }, [displayText, isTyping, currentRole, roles]);

  const navigateToPage = (path) => {
    navigate(path);
  };

  return (
    <section 
      id="hero" 
      className="hero-section"
      style={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        background: `
          radial-gradient(ellipse at top, rgba(16, 31, 67, 0.8) 0%, rgba(6, 11, 25, 0.9) 50%, rgba(2, 4, 12, 1) 100%),
          linear-gradient(135deg, #0f172a 0%, #1e293b 25%, #334155 50%, #1e293b 75%, #0f172a 100%)
        `,
        position: 'relative',
        overflow: 'hidden',
        color: 'white'
      }}
    >
      {/* Animated Space Background */}
      <div 
        className="space-background"
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          overflow: 'hidden',
          zIndex: 1
        }}
      >
        {/* Stars Layer 1 */}
        <div 
          className="stars-layer-1"
          style={{
            position: 'absolute',
            width: '200%',
            height: '200%',
            background: `
              radial-gradient(2px 2px at 20px 30px, #ffffff, transparent),
              radial-gradient(2px 2px at 40px 70px, rgba(255,255,255,0.8), transparent),
              radial-gradient(1px 1px at 90px 40px, #ffffff, transparent),
              radial-gradient(1px 1px at 130px 80px, rgba(255,255,255,0.6), transparent),
              radial-gradient(2px 2px at 160px 30px, #ffffff, transparent)
            `,
            backgroundRepeat: 'repeat',
            backgroundSize: '200px 100px',
            animation: 'twinkle 4s ease-in-out infinite alternate, drift 20s linear infinite'
          }}
        />
        
        {/* Stars Layer 2 */}
