                {/* Inner Geometric Pattern */}
                <div style={{
                  width: '160px',
                  height: '160px',
                  borderRadius: '50%',
                  background: 'rgba(0, 10, 30, 0.8)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  position: 'relative',
                  backdropFilter: 'blur(10px)'
                }}>
                  {/* Central Neural Hub */}
                  <div style={{
                    width: '60px',
                    height: '60px',
                    borderRadius: '50%',
                    background: 'radial-gradient(circle at center, rgba(255, 255, 255, 0.9) 0%, rgba(59, 130, 246, 0.6) 100%)',
                    animation: 'centralPulse 3s ease-in-out infinite',
                    boxShadow: '0 0 30px rgba(255, 255, 255, 0.5)'
                  }} />
                  
                  {/* Orbital Data Points */}
                  {[...Array(8)].map((_, i) => (
                    <div 
                      key={i}
                      style={{
                        position: 'absolute',
                        width: '8px',
                        height: '8px',
                        background: 'rgba(255, 255, 255, 0.8)',
                        borderRadius: '50%',
                        top: '50%',
                        left: '50%',
                        transform: `
                          translate(-50%, -50%) 
                          rotate(${i * 45}deg) 
                          translateY(-${40 + (i % 2) * 10}px)
                        `,
                        animation: `orbitData 4s linear infinite ${i * 0.5}s`,
                        boxShadow: '0 0 8px rgba(255, 255, 255, 0.6)'
                      }}
                    />
                  ))}
                </div>
              </div>

              {/* Floating Neural Elements */}
              <div 
                style={{
                  position: 'absolute',
                  top: '20%',
                  right: '10%',
                  width: '60px',
                  height: '60px',
                  borderRadius: '50%',
                  background: 'radial-gradient(circle at center, rgba(251, 191, 36, 0.6) 0%, rgba(251, 191, 36, 0.1) 100%)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  animation: 'float 3s ease-in-out infinite',
                  border: '2px solid rgba(251, 191, 36, 0.4)',
                  boxShadow: '0 0 20px rgba(251, 191, 36, 0.3)'
                }}
              >
                <div style={{
                  width: '20px',
                  height: '20px',
                  background: 'conic-gradient(from 0deg, rgba(251, 191, 36, 0.8) 0deg, transparent 180deg, rgba(251, 191, 36, 0.8) 360deg)',
                  borderRadius: '50%',
                  animation: 'spin 2s linear infinite'
                }} />
              </div>

              <div 
                style={{
                  position: 'absolute',
                  bottom: '15%',
                  left: '15%',
                  width: '50px',
                  height: '50px',
                  borderRadius: '50%',
                  background: 'radial-gradient(circle at center, rgba(124, 58, 237, 0.6) 0%, rgba(124, 58, 237, 0.1) 100%)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  animation: 'float 3s ease-in-out infinite 1s',
                  border: '2px solid rgba(124, 58, 237, 0.4)',
                  boxShadow: '0 0 20px rgba(124, 58, 237, 0.3)'
                }}
              >
                <div style={{
                  width: '0',
                  height: '0',
                  borderLeft: '8px solid transparent',
                  borderRight: '8px solid transparent',
                  borderBottom: '14px solid rgba(124, 58, 237, 0.8)',
                  animation: 'trianglePulse 2s ease-in-out infinite'
                }} />
              </div>
            </div>
          </div>
        </div>

        {/* Scroll Indicator */}
        <div 
          className="scroll-indicator"
          style={{
            position: 'absolute',
            bottom: '2rem',
            left: '50%',
            transform: 'translateX(-50%)',
            animation: 'bounce 2s infinite'
          }}
        >
          <i 
            className="fas fa-chevron-down"
            style={{
              fontSize: '1.5rem',
              opacity: 0.7,
              cursor: 'pointer'
            }}
            onClick={() => navigateToPage('/about')}
          ></i>
        </div>
      </div>

      {/* Advanced CSS Animations */}
      <style jsx>{`
        @keyframes neuralPulse {
          0%, 100% { 
            transform: scale(1);
            opacity: 0.8;
          }
          50% { 
            transform: scale(1.1);
            opacity: 1;
          }
        }

        @keyframes dataPulse {
          0%, 100% { 
            opacity: 0.6;
            transform: translate(-50%, -50%) scale(1);
          }
          50% { 
            opacity: 1;
            transform: translate(-50%, -50%) scale(1.3);
          }
        }

        @keyframes dataFlow {
          0% { stroke-dashoffset: 0; }
          100% { stroke-dashoffset: -50; }
        }

        @keyframes particleFloat {
          0%, 100% { 
            transform: translate(0, 0);
            opacity: 0.4;
          }
          33% { 
            transform: translate(30px, -20px);
            opacity: 0.8;
          }
          66% { 
            transform: translate(-20px, -40px);
            opacity: 0.6;
          }
        }

        @keyframes profileRotate {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }

        @keyframes centralPulse {
          0%, 100% { 
            transform: scale(1);
            box-shadow: 0 0 30px rgba(255, 255, 255, 0.5);
          }
          50% { 
            transform: scale(1.1);
            box-shadow: 0 0 40px rgba(255, 255, 255, 0.8);
          }
        }

        @keyframes orbitData {
          from { transform: translate(-50%, -50%) rotate(0deg) translateY(-40px); }
          to { transform: translate(-50%, -50%) rotate(360deg) translateY(-40px); }
        }

        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }

        @keyframes trianglePulse {
          0%, 100% { 
            transform: scale(1);
            opacity: 0.8;
          }
          50% { 
            transform: scale(1.2);
            opacity: 1;
          }
        }

        @keyframes blink {
          0%, 50% { opacity: 1; }
          51%, 100% { opacity: 0; }
        }

        @keyframes float {
          0%, 100% { transform: translateY(0px); }
          50% { transform: translateY(-20px); }
        }

        @keyframes bounce {
          0%, 20%, 50%, 80%, 100% { transform: translateX(-50%) translateY(0); }
          40% { transform: translateX(-50%) translateY(-10px); }
          60% { transform: translateX(-50%) translateY(-5px); }
        }

        @keyframes rotate {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }

        @keyframes slowRotate {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }

        @keyframes twinkle {
          0% { opacity: 0.3; }
          50% { opacity: 1; }
          100% { opacity: 0.3; }
        }

        @keyframes drift {
          from { transform: translateX(-100px); }
          to { transform: translateX(100px); }
        }

        @keyframes pulse {
          0%, 100% { 
            transform: scale(1);
            opacity: 0.8;
          }
          50% { 
            transform: scale(1.05);
            opacity: 1;
          }
        }

        /* Glassmorphism hover effects */
        .stat:hover {
          transform: translateY(-4px) !important;
          box-shadow: 
            0 8px 32px rgba(59, 130, 246, 0.3),
            0 0 40px rgba(59, 130, 246, 0.2),
            inset 0 1px 0 rgba(255, 255, 255, 0.2) !important;
        }

        .tech-icon:hover {
          transform: translateY(-5px) scale(1.1) !important;
          box-shadow: 0 10px 30px rgba(59, 130, 246, 0.4) !important;
        }

        @media (max-width: 768px) {
          .hero-content {
            grid-template-columns: 1fr !important;
            text-align: center;
            gap: 2rem !important;
          }
          
          .hero-stats {
            justify-content: center !important;
          }
          
          .hero-buttons {
            justify-content: center !important;
          }
          
          .profile-container {
            width: 250px !important;
            height: 250px !important;
          }
        }
      `}</style>
    </section>
  );
};

export default Hero;
