import React from 'react';
import { Link } from 'react-router-dom';
import { blogPosts } from '../data/blogData';

const Home = () => {
  return (
    <div>
      {/* Hero Section */}
      <section className="hero">
        <div className="container">
          <h1>Quantitative Investment Analysis Blog</h1>
          <p>Learn the cutting-edge algorithms and methods in quantitative investment analysis.</p>
        </div>
      </section>

      {/* Strategy Cards */}
      <section className="container">
        <div className="card-grid">
          {blogPosts.map((post) => (
            <Link to={`/blog/${post.slug}`} key={post.id} className="card">
              <img 
                src={`/assets/images/${post.image}`} 
                alt={post.title}
                className="card-image"
              />
              <h3 className="card-title">{post.title}</h3>
              <p className="card-excerpt">{post.excerpt}</p>
              <div className="card-meta">
                <span>{post.category}</span>
                <span>{post.date}</span>
              </div>
            </Link>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
