import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

/**
 * Modern Hero Section Component
 * Clean, organized, and compact design focused on content and performance
 * 
 * @component
 * @example
 * return <Hero />
 */
const Hero = () => {
  const navigate = useNavigate();
  const [currentRole, setCurrentRole] = useState(0);
  const [displayText, setDisplayText] = useState('');
  const [isTyping, setIsTyping] = useState(true);

  // Professional role titles optimized for impact
  const roles = [
    'Senior Data Scientist',
    'AI Engineer', 
    'Machine Learning Expert',
    'Deep Learning Specialist',
    'NLP Engineer'
  ];

  /**
   * Optimized typing effect with smooth performance
   * Reduced complexity for better user experience
   */
  useEffect(() => {
    let timeout;
    const role = roles[currentRole];

    if (isTyping) {
      if (displayText.length < role.length) {
        timeout = setTimeout(() => {
          setDisplayText(role.slice(0, displayText.length + 1));
        }, 100);
      } else {
        timeout = setTimeout(() => setIsTyping(false), 2000);
      }
    } else {
      if (displayText.length > 0) {
        timeout = setTimeout(() => {
          setDisplayText(displayText.slice(0, -1));
        }, 50);
      } else {
        setCurrentRole((prev) => (prev + 1) % roles.length);
        setIsTyping(true);
      }
    }

    return () => clearTimeout(timeout);
  }, [displayText, isTyping, currentRole, roles]);

  /**
   * Navigation handler
   * @param {string} path - Route path to navigate to
   */
  const navigateToPage = (path) => {
    navigate(path);
  };

  return (
    <section 
      id="hero" 
      className="hero-section"
      style={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        position: 'relative',
        background: `
          linear-gradient(135deg, 
            #0f172a 0%, 
            #1e293b 25%, 
            #334155 50%, 
            #1e293b 75%, 
            #0f172a 100%
          )
        `,
        color: 'white',
        overflow: 'hidden'
      }}
    >
      {/* Clean Background with Subtle Animation */}
      <div 
        className="background-overlay"
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: `
            radial-gradient(circle at 20% 50%, rgba(59, 130, 246, 0.1) 0%, transparent 50%),
            radial-gradient(circle at 80% 20%, rgba(147, 51, 234, 0.1) 0%, transparent 50%),
            radial-gradient(circle at 40% 80%, rgba(16, 185, 129, 0.1) 0%, transparent 50%)
          `,
          animation: 'gentleFloat 20s ease-in-out infinite'
        }}
      />

      {/* Modern Geometric Elements */}
      <div 
        className="geometric-elements"
        style={{
          position: 'absolute',
          top: '10%',
          right: '10%',
          width: '300px',
          height: '300px',
          pointerEvents: 'none',
          zIndex: 1
        }}
      >
        {/* Clean Circular Elements */}
        <div
          style={{
            position: 'absolute',
            width: '100%',
            height: '100%',
            border: '2px solid rgba(59, 130, 246, 0.2)',
            borderRadius: '50%',
            animation: 'slowRotate 30s linear infinite'
          }}
        />
        <div
          style={{
            position: 'absolute',
            top: '20%',
            left: '20%',
            width: '60%',
            height: '60%',
            border: '1px solid rgba(147, 51, 234, 0.3)',
            borderRadius: '50%',
            animation: 'slowRotate 20s linear infinite reverse'
          }}
        />
        <div
          style={{
            position: 'absolute',
            top: '40%',
            left: '40%',
            width: '20%',
