import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getBlogPosts, getFeaturedPosts } from '../data/blogData';

const Home = () => {
  const [blogPosts, setBlogPosts] = useState([]);
  const [featuredPosts, setFeaturedPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadPosts = async () => {
      try {
        const [posts, featured] = await Promise.all([
          getBlogPosts(),
          getFeaturedPosts()
        ]);
        setBlogPosts(posts);
        setFeaturedPosts(featured);
      } catch (error) {
        console.error('Error loading blog posts:', error);
      } finally {
        setLoading(false);
      }
    };

    loadPosts();
  }, []);

  if (loading) {
    return (
      <div>
        <section className="hero-enhanced">
          <div className="container">
            <div className="hero-content">
              <h1>Quantitative Investment Analysis</h1>
              <p>Exploring the intersection of mathematics, statistics, and finance through algorithmic trading strategies.</p>
            </div>
          </div>
        </section>
        <div className="container" style={{ padding: '4rem 0', textAlign: 'center' }}>
          <div className="loading-spinner"></div>
          <p style={{ marginTop: '1rem', color: '#718096' }}>Loading insights...</p>
        </div>
      </div>
    );
  }

  return (
    <div>
      {/* Enhanced Hero Section */}
      <section className="hero-enhanced">
        <div className="container">
          <div className="hero-content">
            <div className="hero-badge">
              <span>Quantitative Finance</span>
            </div>
            <h1>Where Mathematics Meets Markets</h1>
            <p>
              Exploring sophisticated algorithms and statistical methods that drive modern 
              quantitative investment strategies. From portfolio optimization to algorithmic trading.
            </p>
            <div className="hero-stats">
              <div className="stat">
                <span className="stat-number">{blogPosts.length}</span>
                <span className="stat-label">Research Articles</span>
              </div>
              <div className="stat">
                <span className="stat-number">4+</span>
                <span className="stat-label">Trading Strategies</span>
              </div>
              <div className="stat">
                <span className="stat-number">∞</span>
                <span className="stat-label">Possibilities</span>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Featured Articles */}
      {featuredPosts.length > 0 && (
        <section className="featured-section">
          <div className="container">
            <div className="section-header">
              <h2>Featured Research</h2>
              <p>In-depth analysis of cutting-edge quantitative strategies</p>
            </div>
            <div className="featured-grid">
              {featuredPosts.map((post) => (
                <Link to={`/blog/${post.slug}`} key={post.id} className="featured-card">
                  <div className="featured-image">
                    <img 
                      src={`/assets/images/${post.image}`} 
                      alt={post.title}
                    />
                    <div className="featured-overlay">
                      <span className="featured-category">{post.category}</span>
                    </div>
                  </div>
                  <div className="featured-content">
                    <h3>{post.title}</h3>
                    <p>{post.excerpt}</p>
                    <div className="featured-meta">
                      <span>{post.date}</span>
                      <span>•</span>
                      <span>{post.estimatedReadingTime}</span>
                    </div>
                  </div>
                </Link>
              ))}
            </div>
          </div>
        </section>
      )}

      {/* All Articles */}
      <section className="articles-section">
        <div className="container">
          <div className="section-header">
            <h2>Latest Insights</h2>
            <p>Comprehensive guides and analytical frameworks</p>
          </div>
          <div className="articles-grid">
            {blogPosts.map((post) => (
              <Link to={`/blog/${post.slug}`} key={post.id} className="article-card">
                <div className="article-image">
                  <img 
                    src={`/assets/images/${post.image}`} 
                    alt={post.title}
                  />
                </div>
                <div className="article-content">
                  <div className="article-category">
                    <span>{post.category}</span>
                  </div>
                  <h3>{post.title}</h3>
                  <p>{post.excerpt}</p>
                  <div className="article-tags">
                    {post.tags && post.tags.slice(0, 2).map(tag => (
                      <span key={tag} className="article-tag">{tag}</span>
                    ))}
                  </div>
                  <div className="article-meta">
                    <span>{post.date}</span>
                    <span className="read-more">Read Article →</span>
                  </div>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
