import { marked } from 'marked';

// Configure marked options
marked.setOptions({
  breaks: true,
  gfm: true,
});

// Simple frontmatter parser
const parseFrontmatter = (text) => {
  const match = text.match(/^---\s*\n([\s\S]*?)\n---\s*\n([\s\S]*)$/);
  if (!match) {
    return { data: {}, content: text };
  }

  const frontmatterText = match[1];
  const content = match[2];
  const data = {};
  let currentKey = null;

  // Parse YAML-like frontmatter
  frontmatterText.split('\n').forEach(line => {
    const trimmedLine = line.trim();
    if (trimmedLine && trimmedLine.includes(':') && !trimmedLine.startsWith('-')) {
      // Key-value pair
      const [key, ...valueParts] = trimmedLine.split(':');
      const value = valueParts.join(':').trim().replace(/^["']|["']$/g, '');
      currentKey = key.trim();
      if (value) {
        data[currentKey] = value;
      } else {
        data[currentKey] = [];
      }
    } else if (trimmedLine.startsWith('-') && currentKey) {
      // Array item
      const value = trimmedLine.substring(1).trim();
      if (!Array.isArray(data[currentKey])) {
        data[currentKey] = [];
      }
      data[currentKey].push(value);
    }
  });

  return { data, content };
};

export const parseMarkdown = async (markdownPath) => {
  try {
    const response = await fetch(markdownPath);
    if (!response.ok) {
      throw new Error(`Failed to fetch ${markdownPath}: ${response.status}`);
    }
    const markdownText = await response.text();
    
    // Parse frontmatter and content
    const { data: frontmatter, content } = parseFrontmatter(markdownText);
    
    // Convert markdown to HTML
    const htmlContent = marked(content);
    
    return {
      frontmatter,
      content: htmlContent,
      rawContent: content
    };
  } catch (error) {
    console.error('Error parsing markdown:', error);
    return null;
  }
};

