import React, { useState, useEffect } from 'react';
import { useParams, Link, Navigate } from 'react-router-dom';
import { getPostBySlug } from '../data/blogData';
import { parseMarkdown } from '../utils/markdownParser';

const BlogPost = () => {
  const { slug } = useParams();
  const [post, setPost] = useState(null);
  const [content, setContent] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadPost = async () => {
      const postMetadata = getPostBySlug(slug);
      
      if (!postMetadata) {
        setLoading(false);
        return;
      }

      setPost(postMetadata);

      // Load markdown content
      try {
        const markdownPath = `/blogs/${slug}.md`;
        const parsedMarkdown = await parseMarkdown(markdownPath);
        
        if (parsedMarkdown) {
          setContent(parsedMarkdown.content);
        }
      } catch (error) {
        console.error('Error loading blog post:', error);
      }
      
      setLoading(false);
    };

    loadPost();
  }, [slug]);

  if (loading) {
    return (
      <div className="container" style={{ padding: '4rem 0', textAlign: 'center' }}>
        <p>Loading...</p>
      </div>
    );
  }

  if (!post) {
    return <Navigate to="/blog" replace />;
  }

  return (
    <div>
      {/* Breadcrumb */}
      <div className="container" style={{ paddingTop: '2rem' }}>
        <nav style={{ marginBottom: '2rem' }}>
          <Link to="/" style={{ color: '#718096' }}>Home</Link>
          <span style={{ margin: '0 0.5rem', color: '#a0aec0' }}>›</span>
          <Link to="/blog" style={{ color: '#718096' }}>Blog</Link>
          <span style={{ margin: '0 0.5rem', color: '#a0aec0' }}>›</span>
          <span style={{ color: '#2d3748' }}>{post.title}</span>
        </nav>
      </div>

      {/* Blog Post */}
      <article className="container-sm">
        <div className="blog-post">
          {/* Header */}
          <header className="blog-header">
            <h1>{post.title}</h1>
            <p style={{ fontSize: '1.25rem', color: '#718096', marginBottom: '1rem' }}>
              {post.description}
            </p>
            <div className="blog-meta">
              <span className="tag">{post.category}</span>
              <span>{post.date}</span>
              <span>By {post.author}</span>
            </div>
          </header>

          {/* Featured Image */}
          <div style={{ textAlign: 'center', marginBottom: '3rem' }}>
            <img 
              src={`/assets/images/${post.image}`} 
              alt={post.title}
              style={{
                width: '100%',
                maxWidth: '600px',
                height: 'auto',
                borderRadius: '12px',
                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
              }}
            />
          </div>

          {/* Content */}
          <div 
            className="blog-content"
            dangerouslySetInnerHTML={{ __html: content }}
          />

          {/* Tags */}
          <div style={{ marginTop: '3rem', paddingTop: '2rem', borderTop: '1px solid #e2e8f0' }}>
            <h6 style={{ marginBottom: '1rem', color: '#4a5568' }}>Tags:</h6>
            <div className="tags">
              {post.tags.map(tag => (
                <span key={tag} className="tag">{tag}</span>
              ))}
            </div>
          </div>

          {/* Navigation */}
          <div style={{ 
            marginTop: '3rem', 
            paddingTop: '2rem', 
            borderTop: '1px solid #e2e8f0',
            textAlign: 'center'
          }}>
            <Link 
              to="/blog" 
              style={{
                display: 'inline-block',
                padding: '0.75rem 2rem',
                background: '#f7fafc',
                color: '#2d3748',
                borderRadius: '8px',
                border: '1px solid #e2e8f0',
                transition: 'all 0.2s ease'
              }}
              onMouseOver={(e) => {
                e.target.style.background = '#e2e8f0';
              }}
              onMouseOut={(e) => {
                e.target.style.background = '#f7fafc';
              }}
            >
              ← Back to Blog
            </Link>
          </div>
        </div>
      </article>
    </div>
  );
};

export default BlogPost;
