import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

/**
 * Hero Section Component
 * Features dynamic typing effect and compelling call-to-action
 */
const Hero = () => {
  const navigate = useNavigate();
  const [currentRole, setCurrentRole] = useState(0);
  const [displayText, setDisplayText] = useState('');
  const [isTyping, setIsTyping] = useState(true);

  const roles = [
    'Senior Data Scientist',
    'AI Engineer', 
    'Machine Learning Expert',
    'Deep Learning Specialist',
    'NLP Engineer'
  ];

  useEffect(() => {
    let timeout;
    const role = roles[currentRole];
    
    if (isTyping) {
      if (displayText.length < role.length) {
        timeout = setTimeout(() => {
          setDisplayText(role.slice(0, displayText.length + 1));
        }, 100);
      } else {
        timeout = setTimeout(() => {
          setIsTyping(false);
        }, 2000);
      }
    } else {
      if (displayText.length > 0) {
        timeout = setTimeout(() => {
          setDisplayText(displayText.slice(0, -1));
        }, 50);
      } else {
        setCurrentRole((prev) => (prev + 1) % roles.length);
        setIsTyping(true);
      }
    }

    return () => clearTimeout(timeout);
  }, [displayText, isTyping, currentRole, roles]);

  const navigateToPage = (path) => {
    navigate(path);
  };

  return (
    <section 
      id="hero" 
      className="hero-section"
      style={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        background: `
          radial-gradient(ellipse at top, rgba(16, 31, 67, 0.8) 0%, rgba(6, 11, 25, 0.9) 50%, rgba(2, 4, 12, 1) 100%),
          linear-gradient(135deg, #0f172a 0%, #1e293b 25%, #334155 50%, #1e293b 75%, #0f172a 100%)
        `,
        position: 'relative',
        overflow: 'hidden',
        color: 'white'
      }}
    >
      {/* Animated Space Background */}
      <div 
        className="space-background"
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          overflow: 'hidden',
          zIndex: 1
        }}
      >
        {/* Stars Layer 1 */}
        <div 
          className="stars-layer-1"
          style={{
            position: 'absolute',
            width: '200%',
            height: '200%',
            background: `
              radial-gradient(2px 2px at 20px 30px, #ffffff, transparent),
              radial-gradient(2px 2px at 40px 70px, rgba(255,255,255,0.8), transparent),
              radial-gradient(1px 1px at 90px 40px, #ffffff, transparent),
              radial-gradient(1px 1px at 130px 80px, rgba(255,255,255,0.6), transparent),
              radial-gradient(2px 2px at 160px 30px, #ffffff, transparent)
            `,
            backgroundRepeat: 'repeat',
            backgroundSize: '200px 100px',
            animation: 'twinkle 4s ease-in-out infinite alternate, drift 20s linear infinite'
          }}
        />
        
        {/* Stars Layer 2 */}
        <div 
          className="stars-layer-2"
          style={{
            position: 'absolute',
            width: '200%',
            height: '200%',
            background: `
              radial-gradient(1px 1px at 50px 50px, rgba(100,200,255,0.8), transparent),
              radial-gradient(2px 2px at 120px 20px, rgba(100,200,255,0.6), transparent),
              radial-gradient(1px 1px at 170px 90px, rgba(100,200,255,0.4), transparent)
            `,
            backgroundRepeat: 'repeat',
            backgroundSize: '220px 120px',
            animation: 'twinkle 3s ease-in-out infinite alternate-reverse, drift 25s linear infinite reverse'
          }}
        />

        {/* Floating Particles */}
        <div 
          className="particles"
          style={{
            position: 'absolute',
            width: '100%',
            height: '100%',
            background: `
              radial-gradient(circle at 25% 25%, rgba(59, 130, 246, 0.15) 0%, transparent 2%),
              radial-gradient(circle at 75% 75%, rgba(147, 51, 234, 0.1) 0%, transparent 2%),
              radial-gradient(circle at 50% 80%, rgba(34, 197, 94, 0.08) 0%, transparent 2%),
              radial-gradient(circle at 80% 20%, rgba(249, 115, 22, 0.1) 0%, transparent 2%)
            `,
            backgroundSize: '400px 400px, 350px 350px, 450px 450px, 300px 300px',
            animation: 'float 8s ease-in-out infinite, rotate 30s linear infinite'
          }}
        />

        {/* Ambient Light Beams */}
        <div 
          className="light-beams"
          style={{
            position: 'absolute',
            top: '-50%',
            left: '-50%',
            width: '200%',
            height: '200%',
            background: `
              conic-gradient(from 45deg at 50% 50%, 
                transparent 0deg, 
                rgba(59, 130, 246, 0.03) 60deg, 
                transparent 120deg,
                rgba(147, 51, 234, 0.02) 180deg,
                transparent 240deg,
                rgba(34, 197, 94, 0.03) 300deg,
                transparent 360deg
              )
            `,
            animation: 'slowRotate 40s linear infinite'
          }}
        />

        {/* Nebula Effect */}
        <div 
          className="nebula"
          style={{
            position: 'absolute',
            top: '20%',
            right: '10%',
            width: '60%',
            height: '60%',
            background: `
              radial-gradient(ellipse at center, 
                rgba(59, 130, 246, 0.08) 0%, 
                rgba(147, 51, 234, 0.05) 30%, 
                transparent 70%
              )
            `,
            filter: 'blur(80px)',
            animation: 'pulse 6s ease-in-out infinite'
          }}
        />
      </div>

      <div className="container" style={{ position: 'relative', zIndex: 2 }}>
        <div 
          className="hero-content"
          style={{
            display: 'grid',
            gridTemplateColumns: '1fr 1fr',
            gap: '4rem',
            alignItems: 'center',
            position: 'relative',
            zIndex: 1
          }}
        >
          {/* Left Content */}
          <div className="hero-text">
            <h1 
              style={{
                fontSize: 'clamp(2.5rem, 5vw, 4rem)',
                fontWeight: '800',
                marginBottom: '1rem',
                lineHeight: '1.1',
                textShadow: `
                  0 0 20px rgba(255, 255, 255, 0.3),
                  0 0 40px rgba(59, 130, 246, 0.2),
                  0 4px 8px rgba(0, 0, 0, 0.3)
                `,
                background: 'linear-gradient(135deg, #ffffff 0%, #e2e8f0 50%, #ffffff 100%)',
                backgroundClip: 'text',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                filter: 'drop-shadow(0 2px 4px rgba(0, 0, 0, 0.3))'
              }}
            >
              Hi, I'm{' '}
              <span 
                style={{ 
                  background: 'linear-gradient(135deg, #f59e0b 0%, #fbbf24 50%, #f59e0b 100%)',
                  backgroundClip: 'text',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  textShadow: `
                    0 0 30px rgba(245, 158, 11, 0.5),
                    0 0 60px rgba(245, 158, 11, 0.3),
                    0 4px 8px rgba(0, 0, 0, 0.3)
                  `,
                  filter: 'drop-shadow(0 0 10px rgba(245, 158, 11, 0.4))'
                }}
              >
                Soheil
              </span>
              <span style={{ display: 'block', marginTop: '0.5rem' }}>
                <span 
                  style={{ 
                    background: 'linear-gradient(135deg, #f59e0b 0%, #fbbf24 50%, #f59e0b 100%)',
                    backgroundClip: 'text',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    textShadow: `
                      0 0 30px rgba(245, 158, 11, 0.5),
                      0 0 60px rgba(245, 158, 11, 0.3),
                      0 4px 8px rgba(0, 0, 0, 0.3)
                    `,
                    filter: 'drop-shadow(0 0 10px rgba(245, 158, 11, 0.4))'
                  }}
                >
                  {displayText}
                </span>
                <span 
                  style={{ 
                    opacity: isTyping ? 1 : 0,
                    animation: 'blink 1s infinite',
                    background: 'linear-gradient(135deg, #f59e0b 0%, #fbbf24 50%, #f59e0b 100%)',
                    backgroundClip: 'text',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    filter: 'drop-shadow(0 0 10px rgba(245, 158, 11, 0.6))'
                  }}
                >
                  |
                </span>
              </span>
            </h1>

            <p 
              style={{
                fontSize: '1.25rem',
                marginBottom: '2rem',
                opacity: 0.9,
                maxWidth: '500px',
                lineHeight: '1.6',
                color: 'rgba(226, 232, 240, 0.95)',
                textShadow: '0 2px 4px rgba(0, 0, 0, 0.5)',
                fontWeight: '400'
              }}
            >
              6+ years of experience across the full AI/ML spectrum. From building trading strategies to teaching AI, I create intelligent solutions that make a real impact.
            </p>

            <div 
              className="hero-stats"
              style={{
                display: 'flex',
                gap: '2rem',
                marginBottom: '2rem',
                flexWrap: 'wrap'
              }}
            >
              <div 
                className="stat"
                style={{
                  textAlign: 'center',
                  padding: '1rem',
                  background: 'rgba(255, 255, 255, 0.05)',
                  borderRadius: '12px',
                  border: '1px solid rgba(59, 130, 246, 0.2)',
                  backdropFilter: 'blur(10px)',
                  boxShadow: `
                    0 4px 6px rgba(0, 0, 0, 0.1),
                    0 0 20px rgba(59, 130, 246, 0.1),
                    inset 0 1px 0 rgba(255, 255, 255, 0.1)
                  `,
                  transition: 'all 0.3s ease'
                }}
              >
                <div 
                  style={{ 
                    fontSize: '2rem', 
                    fontWeight: '700',
                    background: 'linear-gradient(135deg, #f59e0b 0%, #fbbf24 100%)',
                    backgroundClip: 'text',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    textShadow: '0 0 20px rgba(245, 158, 11, 0.5)',
                    filter: 'drop-shadow(0 2px 4px rgba(0, 0, 0, 0.3))'
                  }}
                >
                  15+
                </div>
                <div style={{ fontSize: '0.9rem', opacity: 0.8, color: '#e2e8f0' }}>AI Projects</div>
              </div>
              <div 
                className="stat"
                style={{
                  textAlign: 'center',
                  padding: '1rem',
                  background: 'rgba(255, 255, 255, 0.05)',
                  borderRadius: '12px',
                  border: '1px solid rgba(59, 130, 246, 0.2)',
                  backdropFilter: 'blur(10px)',
                  boxShadow: `
                    0 4px 6px rgba(0, 0, 0, 0.1),
                    0 0 20px rgba(59, 130, 246, 0.1),
                    inset 0 1px 0 rgba(255, 255, 255, 0.1)
                  `,
                  transition: 'all 0.3s ease'
                }}
              >
                <div 
                  style={{ 
                    fontSize: '2rem', 
                    fontWeight: '700',
                    background: 'linear-gradient(135deg, #f59e0b 0%, #fbbf24 100%)',
                    backgroundClip: 'text',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    textShadow: '0 0 20px rgba(245, 158, 11, 0.5)',
                    filter: 'drop-shadow(0 2px 4px rgba(0, 0, 0, 0.3))'
                  }}
                >
                  6+
                </div>
                <div style={{ fontSize: '0.9rem', opacity: 0.8, color: '#e2e8f0' }}>Years Experience</div>
              </div>
              <div 
                className="stat"
                style={{
                  textAlign: 'center',
                  padding: '1rem',
                  background: 'rgba(255, 255, 255, 0.05)',
                  borderRadius: '12px',
                  border: '1px solid rgba(59, 130, 246, 0.2)',
                  backdropFilter: 'blur(10px)',
                  boxShadow: `
                    0 4px 6px rgba(0, 0, 0, 0.1),
                    0 0 20px rgba(59, 130, 246, 0.1),
                    inset 0 1px 0 rgba(255, 255, 255, 0.1)
                  `,
                  transition: 'all 0.3s ease'
                }}
              >
                <div 
                  style={{ 
                    fontSize: '2rem', 
                    fontWeight: '700',
                    background: 'linear-gradient(135deg, #f59e0b 0%, #fbbf24 100%)',
                    backgroundClip: 'text',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    textShadow: '0 0 20px rgba(245, 158, 11, 0.5)',
                    filter: 'drop-shadow(0 2px 4px rgba(0, 0, 0, 0.3))'
                  }}
                >
                  20+
                </div>
                <div style={{ fontSize: '0.9rem', opacity: 0.8, color: '#e2e8f0' }}>Certifications</div>
              </div>
            </div>

            <div 
              className="hero-buttons"
              style={{
                display: 'flex',
                gap: '1rem',
                flexWrap: 'wrap'
              }}
            >
              <button 
                className="btn btn--primary"
                onClick={() => navigateToPage('/projects')}
                style={{
                  padding: '1rem 2rem',
                  fontSize: '1.1rem',
                  fontWeight: '600',
                  background: `
                    linear-gradient(135deg, 
                      rgba(245, 158, 11, 0.9) 0%, 
                      rgba(251, 191, 36, 0.9) 50%, 
                      rgba(245, 158, 11, 0.9) 100%
                    )
                  `,
                  color: '#0f172a',
                  border: '1px solid rgba(245, 158, 11, 0.3)',
                  borderRadius: '12px',
                  cursor: 'pointer',
                  transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                  backdropFilter: 'blur(10px)',
                  boxShadow: `
                    0 4px 6px rgba(0, 0, 0, 0.1),
                    0 0 20px rgba(245, 158, 11, 0.3),
                    inset 0 1px 0 rgba(255, 255, 255, 0.2)
                  `,
                  position: 'relative',
                  overflow: 'hidden'
                }}
                onMouseEnter={(e) => {
                  e.target.style.transform = 'translateY(-2px) scale(1.02)';
                  e.target.style.boxShadow = '0 8px 25px rgba(245, 158, 11, 0.4)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.transform = 'translateY(0) scale(1)';
                  e.target.style.boxShadow = `
                    0 4px 6px rgba(0, 0, 0, 0.1),
                    0 0 20px rgba(245, 158, 11, 0.3),
                    inset 0 1px 0 rgba(255, 255, 255, 0.2)
                  `;
                }}
              >
                <i className="fas fa-rocket" style={{ marginRight: '0.5rem' }}></i>
                Explore My Work
              </button>

              <button 
                className="btn btn--secondary"
                onClick={() => navigateToPage('/contact')}
                style={{
                  padding: '1rem 2rem',
                  fontSize: '1.1rem',
                  fontWeight: '600',
                  background: 'rgba(255, 255, 255, 0.1)',
                  color: 'white',
                  border: '2px solid rgba(255, 255, 255, 0.3)',
                  borderRadius: '12px',
                  cursor: 'pointer',
                  transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                  backdropFilter: 'blur(10px)',
                  boxShadow: `
                    0 4px 6px rgba(0, 0, 0, 0.1),
                    0 0 20px rgba(255, 255, 255, 0.1),
                    inset 0 1px 0 rgba(255, 255, 255, 0.2)
                  `,
                  position: 'relative',
                  overflow: 'hidden'
                }}
                onMouseEnter={(e) => {
                  e.target.style.background = 'rgba(255, 255, 255, 0.2)';
                  e.target.style.borderColor = 'rgba(255, 255, 255, 0.5)';
                  e.target.style.transform = 'translateY(-2px) scale(1.02)';
                  e.target.style.boxShadow = '0 8px 25px rgba(255, 255, 255, 0.2)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.background = 'rgba(255, 255, 255, 0.1)';
                  e.target.style.borderColor = 'rgba(255, 255, 255, 0.3)';
                  e.target.style.transform = 'translateY(0) scale(1)';
                  e.target.style.boxShadow = `
                    0 4px 6px rgba(0, 0, 0, 0.1),
                    0 0 20px rgba(255, 255, 255, 0.1),
                    inset 0 1px 0 rgba(255, 255, 255, 0.2)
                  `;
                }}
              >
                <i className="fas fa-envelope" style={{ marginRight: '0.5rem' }}></i>
                Get In Touch
              </button>

              <a 
                href="/resume.pdf" 
                download
                className="btn btn--outline"
                style={{
                  padding: '1rem 2rem',
                  fontSize: '1.1rem',
                  fontWeight: '600',
                  background: 'transparent',
                  color: 'white',
                  border: '2px solid rgba(255, 255, 255, 0.3)',
                  borderRadius: 'var(--radius-lg)',
                  cursor: 'pointer',
                  transition: 'all var(--transition-normal)',
                  transform: 'translateY(0)',
                  textDecoration: 'none',
                  display: 'inline-flex',
                  alignItems: 'center'
                }}
                onMouseEnter={(e) => {
                  e.target.style.borderColor = 'white';
                  e.target.style.transform = 'translateY(-2px)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.borderColor = 'rgba(255, 255, 255, 0.3)';
                  e.target.style.transform = 'translateY(0)';
                }}
              >
                <i className="fas fa-download" style={{ marginRight: '0.5rem' }}></i>
                Download Resume
              </a>
            </div>
          </div>

          {/* Right Content - Enhanced Profile Visual */}
          <div 
            className="hero-image"
            style={{
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              position: 'relative'
            }}
          >
            {/* Orbital Rings */}
            <div 
              className="orbital-ring-1"
              style={{
                position: 'absolute',
                width: '400px',
                height: '400px',
                borderRadius: '50%',
                border: '1px solid rgba(59, 130, 246, 0.3)',
                animation: 'rotate 20s linear infinite'
              }}
            />
            <div 
              className="orbital-ring-2"
              style={{
                position: 'absolute',
                width: '450px',
                height: '450px',
                borderRadius: '50%',
                border: '1px solid rgba(147, 51, 234, 0.2)',
                animation: 'rotate 30s linear infinite reverse'
              }}
            />
            
            {/* Floating Tech Icons */}
            <div 
              className="tech-icon"
              style={{
                position: 'absolute',
                top: '10%',
                right: '10%',
                width: '40px',
                height: '40px',
                background: 'rgba(59, 130, 246, 0.2)',
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                animation: 'float 3s ease-in-out infinite',
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(59, 130, 246, 0.3)'
              }}
            >
              <i className="fas fa-brain" style={{ color: '#3b82f6', fontSize: '18px' }}></i>
            </div>
            
            <div 
              className="tech-icon"
              style={{
                position: 'absolute',
                bottom: '15%',
                left: '5%',
                width: '35px',
                height: '35px',
                background: 'rgba(34, 197, 94, 0.2)',
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                animation: 'float 4s ease-in-out infinite 1s',
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(34, 197, 94, 0.3)'
              }}
            >
              <i className="fas fa-chart-line" style={{ color: '#22c55e', fontSize: '16px' }}></i>
            </div>

            <div 
              className="tech-icon"
              style={{
                position: 'absolute',
                top: '20%',
                left: '15%',
                width: '38px',
                height: '38px',
                background: 'rgba(147, 51, 234, 0.2)',
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                animation: 'float 3.5s ease-in-out infinite 0.5s',
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(147, 51, 234, 0.3)'
              }}
            >
              <i className="fas fa-robot" style={{ color: '#9333ea', fontSize: '17px' }}></i>
            </div>

            {/* Central Profile Container */}
            <div 
              className="profile-container"
              style={{
                width: '350px',
                height: '350px',
                borderRadius: '50%',
                background: `
                  radial-gradient(circle at 30% 30%, 
                    rgba(59, 130, 246, 0.15) 0%, 
                    rgba(147, 51, 234, 0.1) 50%, 
                    rgba(59, 130, 246, 0.05) 100%
                  )
                `,
                backdropFilter: 'blur(20px)',
                border: '2px solid rgba(255, 255, 255, 0.2)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                position: 'relative',
                overflow: 'hidden',
                boxShadow: `
                  0 20px 40px rgba(0, 0, 0, 0.3),
                  0 0 60px rgba(59, 130, 246, 0.2),
                  inset 0 1px 0 rgba(255, 255, 255, 0.2)
                `,
                animation: 'pulse 4s ease-in-out infinite'
              }}
            >
              {/* Inner Glow Effect */}
              <div 
                style={{
                  position: 'absolute',
                  top: '10%',
                  left: '10%',
                  right: '10%',
                  bottom: '10%',
                  borderRadius: '50%',
                  background: `
                    radial-gradient(circle at center, 
                      rgba(255, 255, 255, 0.1) 0%, 
                      transparent 70%
                    )
                  `,
                  animation: 'rotate 15s linear infinite'
                }}
              />
              
              {/* Profile Avatar */}
              <div 
                style={{
                  width: '200px',
                  height: '200px',
                  borderRadius: '50%',
                  background: `
                    linear-gradient(135deg, 
                      rgba(245, 158, 11, 0.8) 0%, 
                      rgba(147, 51, 234, 0.8) 50%,
                      rgba(59, 130, 246, 0.8) 100%
                    )
                  `,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '4rem',
                  color: 'white',
                  border: '3px solid rgba(255, 255, 255, 0.3)',
                  boxShadow: `
                    0 10px 30px rgba(0, 0, 0, 0.3),
                    inset 0 1px 0 rgba(255, 255, 255, 0.3)
                  `,
                  position: 'relative',
                  zIndex: 2
                }}
              >
                <i className="fas fa-user"></i>
              </div>

              {/* Floating Elements */}
              <div 
                style={{
                  position: 'absolute',
                  top: '20%',
                  right: '10%',
                  width: '60px',
                  height: '60px',
                  borderRadius: '50%',
                  background: 'rgba(251, 191, 36, 0.2)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  animation: 'float 3s ease-in-out infinite'
                }}
              >
                <i className="fas fa-brain" style={{ color: 'var(--accent-amber)', fontSize: '1.5rem' }}></i>
              </div>

              <div 
                style={{
                  position: 'absolute',
                  bottom: '15%',
                  left: '15%',
                  width: '50px',
                  height: '50px',
                  borderRadius: '50%',
                  background: 'rgba(124, 58, 237, 0.2)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  animation: 'float 3s ease-in-out infinite 1s'
                }}
              >
                <i className="fas fa-code" style={{ color: 'var(--accent-purple)', fontSize: '1.2rem' }}></i>
              </div>
            </div>
          </div>
        </div>

        {/* Scroll Indicator */}
        <div 
          className="scroll-indicator"
          style={{
            position: 'absolute',
            bottom: '2rem',
            left: '50%',
            transform: 'translateX(-50%)',
            animation: 'bounce 2s infinite'
          }}
        >
          <i 
            className="fas fa-chevron-down"
            style={{
              fontSize: '1.5rem',
              opacity: 0.7,
              cursor: 'pointer'
            }}
            onClick={() => navigateToPage('/about')}
          ></i>
        </div>
      </div>

      {/* Advanced CSS Animations */}
      <style jsx>{`
        @keyframes blink {
          0%, 50% { opacity: 1; }
          51%, 100% { opacity: 0; }
        }

        @keyframes float {
          0%, 100% { transform: translateY(0px); }
          50% { transform: translateY(-20px); }
        }

        @keyframes bounce {
          0%, 20%, 50%, 80%, 100% { transform: translateX(-50%) translateY(0); }
          40% { transform: translateX(-50%) translateY(-10px); }
          60% { transform: translateX(-50%) translateY(-5px); }
        }

        @keyframes rotate {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }

        @keyframes slowRotate {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }

        @keyframes twinkle {
          0% { opacity: 0.3; }
          50% { opacity: 1; }
          100% { opacity: 0.3; }
        }

        @keyframes drift {
          from { transform: translateX(-100px); }
          to { transform: translateX(100px); }
        }

        @keyframes pulse {
          0%, 100% { 
            transform: scale(1);
            opacity: 0.8;
          }
          50% { 
            transform: scale(1.05);
            opacity: 1;
          }
        }

        /* Glassmorphism hover effects */
        .stat:hover {
          transform: translateY(-4px) !important;
          box-shadow: 
            0 8px 32px rgba(59, 130, 246, 0.3),
            0 0 40px rgba(59, 130, 246, 0.2),
            inset 0 1px 0 rgba(255, 255, 255, 0.2) !important;
        }

        .tech-icon:hover {
          transform: translateY(-5px) scale(1.1) !important;
          box-shadow: 0 10px 30px rgba(59, 130, 246, 0.4) !important;
        }

        @media (max-width: 768px) {
          .hero-content {
            grid-template-columns: 1fr !important;
            text-align: center;
            gap: 2rem !important;
          }
          
          .hero-stats {
            justify-content: center !important;
          }
          
          .hero-buttons {
            justify-content: center !important;
          }
          
          .profile-container {
            width: 250px !important;
            height: 250px !important;
          }
        }
      `}</style>
    </section>
  );
};

export default Hero;
