import React, { useState, useEffect } from 'react';
import { useParams, Link, Navigate } from 'react-router-dom';
import { getBlogPost } from '../data/blogData';
import { parseMarkdown } from '../utils/markdownParser';

const BlogPost = () => {
  const { slug } = useParams();
  const [post, setPost] = useState(null);
  const [content, setContent] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadPost = async () => {
      try {
        console.log('Loading post with slug:', slug);
        const postMetadata = await getPostBySlug(slug);
        console.log('Post metadata:', postMetadata);
        
        if (!postMetadata) {
          console.log('No post metadata found for slug:', slug);
          setLoading(false);
          return;
        }

        setPost(postMetadata);

        // Load markdown content
        const markdownPath = `/blogs/${slug}.md`;
        console.log('Fetching markdown from:', markdownPath);
        const parsedMarkdown = await parseMarkdown(markdownPath);
        console.log('Parsed markdown:', parsedMarkdown);
        
        if (parsedMarkdown) {
          console.log('Setting content, length:', parsedMarkdown.content.length);
          setContent(parsedMarkdown.content);
        } else {
          console.log('No parsed markdown content');
        }
      } catch (error) {
        console.error('Error loading blog post:', error);
      } finally {
        setLoading(false);
      }
    };

    loadPost();
  }, [slug]);

  if (loading) {
    return (
      <div className="container">
        <div style={{ padding: '2rem', textAlign: 'center' }}>
          <p>Loading post...</p>
        </div>
      </div>
    );
  }

  if (!post) {
    return <Navigate to="/" replace />;
  }

  console.log('Rendering BlogPost with content length:', content.length);

  return (
    <div>
      {/* Breadcrumb */}
      <nav className="breadcrumb">
        <div className="container">
          <Link to="/">Home</Link>
          <span> / </span>
          <span>{post.title}</span>
        </div>
      </nav>

      {/* Hero Section */}
      <section className="hero">
        <div className="container">
          <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1rem' }}>
            <span className="tag">{post.category}</span>
            <span style={{ fontSize: '0.875rem', color: '#666' }}>{post.date}</span>
            {post.estimatedReadingTime && (
              <>
                <span style={{ fontSize: '0.875rem', color: '#666' }}>•</span>
                <span style={{ fontSize: '0.875rem', color: '#666' }}>{post.estimatedReadingTime}</span>
              </>
            )}
          </div>
          <h1>{post.title}</h1>
          <p>{post.description}</p>
          {post.tags && post.tags.length > 0 && (
            <div className="tags" style={{ marginTop: '1rem' }}>
              {post.tags.map(tag => (
                <span key={tag} className="tag" style={{ backgroundColor: '#f1f3f4' }}>{tag}</span>
              ))}
            </div>
          )}
        </div>
      </section>

      {/* Blog Content */}
      <article className="container">
        <div className="blog-content">
          {/* Featured Image */}
          {post.image && (
            <div style={{ marginBottom: '2rem', textAlign: 'center' }}>
              <img 
                src={`/assets/images/${post.image}`} 
                alt={post.title}
                style={{
                  width: '100%',
                  maxWidth: '800px',
                  height: 'auto',
                  borderRadius: '8px',
                  boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
                }}
              />
            </div>
          )}
          
          {/* Debug info */}
          {content.length === 0 && (
            <div style={{ padding: '1rem', backgroundColor: '#ffebee', borderRadius: '4px', marginBottom: '2rem' }}>
              <p><strong>Debug Info:</strong></p>
              <p>Content length: {content.length}</p>
              <p>Slug: {slug}</p>
              <p>Post loaded: {post ? 'Yes' : 'No'}</p>
            </div>
          )}
          
          {/* Markdown Content */}
          <div 
            className="markdown-content"
            dangerouslySetInnerHTML={{ __html: content }}
            style={{
              lineHeight: '1.7',
              fontSize: '1.1rem',
              color: '#2d3748'
            }}
          />

          {/* Author Info */}
          <div style={{
            marginTop: '3rem',
            padding: '2rem',
            backgroundColor: '#f8f9fa',
            borderRadius: '8px',
            border: '1px solid #e9ecef'
          }}>
            <h3 style={{ marginBottom: '1rem' }}>About the Author</h3>
            <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
              <div style={{
                width: '60px',
                height: '60px',
                borderRadius: '50%',
                backgroundColor: '#6c5ce7',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                color: 'white',
                fontSize: '1.5rem',
                fontWeight: 'bold'
              }}>
                {post.author ? post.author.charAt(0) : 'S'}
              </div>
              <div>
                <h4 style={{ margin: 0, marginBottom: '0.25rem' }}>{post.author || 'Soheil Mohammadpour'}</h4>
                <p style={{ margin: 0, fontSize: '0.9rem', color: '#666' }}>
                  Quantitative analyst specializing in algorithmic trading strategies and portfolio optimization.
                </p>
              </div>
            </div>
          </div>

          {/* Back to Home */}
          <div style={{ marginTop: '3rem', textAlign: 'center' }}>
            <Link 
              to="/" 
              style={{
                display: 'inline-block',
                padding: '0.75rem 1.5rem',
                backgroundColor: '#2d3748',
                color: 'white',
                textDecoration: 'none',
                borderRadius: '6px',
                transition: 'background-color 0.2s'
              }}
            >
              ← Back to Home
            </Link>
          </div>
        </div>
      </article>
    </div>
  );
};

export default BlogPost;
