import React, { useState, useEffect } from 'react';
import { useLocation, NavLink } from 'react-router-dom';

/**
 * Unified Header Component with Dark Theme
 * Features elegant tab-based navigation with modern dark design
 * Combines traditional header and tab navigation into one cohesive component
 */
const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const location = useLocation();

  // Enhanced navigation items with icons and descriptions
  const navItems = [
    { 
      id: 'home', 
      path: '/', 
      label: 'Home', 
      icon: 'fas fa-home',
      description: 'Welcome & Overview'
    },
    { 
      id: 'about', 
      path: '/about', 
      label: 'About', 
      icon: 'fas fa-user',
      description: 'My Story & Background'
    },
    { 
      id: 'skills', 
      path: '/skills', 
      label: 'Skills', 
      icon: 'fas fa-code',
      description: 'Technical Expertise'
    },
    { 
      id: 'projects', 
      path: '/projects', 
      label: 'Projects', 
      icon: 'fas fa-laptop-code',
      description: 'Featured Work'
    },
    { 
      id: 'experience', 
      path: '/experience', 
      label: 'Experience', 
      icon: 'fas fa-briefcase',
      description: 'Career Journey'
    },
    { 
      id: 'certifications', 
      path: '/certifications', 
      label: 'Certifications', 
      icon: 'fas fa-certificate',
      description: 'Achievements'
    },
    { 
      id: 'contact', 
      path: '/contact', 
      label: 'Contact', 
      icon: 'fas fa-envelope',
      description: 'Get in Touch'
    }
  ];

  // Handle scroll effects for enhanced visual feedback
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 20);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Close mobile menu when route changes
  useEffect(() => {
    setIsMenuOpen(false);
  }, [location.pathname]);

  // Get current active page for enhanced styling
  const getCurrentPage = () => {
    return navItems.find(item => item.path === location.pathname)?.id || 'home';
  };

  return (
    <>
      {/* Unified Compact Header with Dark Theme */}
      <header 
        className="unified-header"
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          zIndex: 1000,
          background: isScrolled 
            ? 'linear-gradient(135deg, rgba(10, 16, 30, 0.98), rgba(24, 28, 42, 0.98))'
            : 'linear-gradient(135deg, rgba(10, 16, 30, 0.95), rgba(24, 28, 42, 0.95))',
          backdropFilter: 'blur(24px)',
          borderBottom: isScrolled 
            ? '1px solid rgba(59, 130, 246, 0.2)' 
            : '1px solid rgba(59, 130, 246, 0.1)',
          transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
          boxShadow: isScrolled 
            ? '0 12px 40px rgba(0, 0, 0, 0.4), 0 4px 16px rgba(59, 130, 246, 0.1)' 
            : '0 8px 28px rgba(0, 0, 0, 0.3), 0 2px 8px rgba(59, 130, 246, 0.05)',
          padding: '1.25rem 0'
        }}
      >
        <div className="container">
          <div 
            className="header-content"
            style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'space-between',
              gap: '2rem'
            }}
          >
            {/* Enhanced Logo */}
            <NavLink 
              to="/"
              className="nav__logo"
              style={{
                fontSize: '1.8rem',
                fontWeight: '900',
                background: 'linear-gradient(135deg, #60a5fa, #a78bfa, #f472b6)',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                backgroundClip: 'text',
                textDecoration: 'none',
                transition: 'all 0.3s ease',
                filter: 'drop-shadow(0 2px 8px rgba(96, 165, 250, 0.3))',
                flexShrink: 0
              }}
              onMouseEnter={(e) => {
                e.target.style.transform = 'scale(1.05)';
                e.target.style.filter = 'drop-shadow(0 4px 12px rgba(96, 165, 250, 0.5))';
              }}
              onMouseLeave={(e) => {
                e.target.style.transform = 'scale(1)';
                e.target.style.filter = 'drop-shadow(0 2px 8px rgba(96, 165, 250, 0.3))';
              }}
            >
              Soheil<span style={{ 
                background: 'linear-gradient(135deg, #fbbf24, #f59e0b)',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                backgroundClip: 'text'
              }}>.</span>
            </NavLink>

            {/* Compact Tab Navigation */}
            <div 
              className="tabs-container"
              style={{
                display: 'flex',
                gap: '0.5rem',
                overflowX: 'auto',
                scrollbarWidth: 'none',
                msOverflowStyle: 'none',
                padding: '0.5rem',
                background: 'rgba(0, 0, 0, 0.2)',
                borderRadius: '20px',
                backdropFilter: 'blur(12px)',
                border: '1px solid rgba(59, 130, 246, 0.1)',
                boxShadow: 'inset 0 2px 8px rgba(0, 0, 0, 0.3)',
                flex: 1,
                maxWidth: '800px'
              }}
            >
              {navItems.map((item) => {
                const isActive = getCurrentPage() === item.id;
                return (
                  <NavLink
                    key={item.id}
                    to={item.path}
                    className={`tab-item ${isActive ? 'tab-item--active' : ''}`}
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      gap: '0.5rem',
                      padding: '0.75rem 1rem',
                      borderRadius: '14px',
                      textDecoration: 'none',
                      fontSize: '0.875rem',
                      fontWeight: '600',
                      transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
                      background: isActive 
                        ? 'linear-gradient(135deg, rgba(59, 130, 246, 0.8), rgba(147, 51, 234, 0.8))'
                        : 'rgba(255, 255, 255, 0.05)',
                      color: isActive ? '#ffffff' : '#cbd5e1',
                      border: isActive 
                        ? '1px solid rgba(59, 130, 246, 0.6)'
                        : '1px solid rgba(255, 255, 255, 0.1)',
                      minWidth: 'max-content',
                      transform: isActive ? 'translateY(-1px)' : 'translateY(0)',
                      boxShadow: isActive 
                        ? '0 8px 25px rgba(59, 130, 246, 0.3), 0 4px 12px rgba(0, 0, 0, 0.4)' 
                        : '0 2px 8px rgba(0, 0, 0, 0.2)',
                      backdropFilter: 'blur(8px)',
                      position: 'relative',
                      overflow: 'hidden'
                    }}
                    onMouseEnter={(e) => {
                      if (!isActive) {
                        e.currentTarget.style.background = 'rgba(255, 255, 255, 0.1)';
                        e.currentTarget.style.borderColor = 'rgba(59, 130, 246, 0.3)';
                        e.currentTarget.style.color = '#f1f5f9';
                        e.currentTarget.style.transform = 'translateY(-1px)';
                        e.currentTarget.style.boxShadow = '0 4px 15px rgba(59, 130, 246, 0.2), 0 2px 8px rgba(0, 0, 0, 0.3)';
                      }
                    }}
                    onMouseLeave={(e) => {
                      if (!isActive) {
                        e.currentTarget.style.background = 'rgba(255, 255, 255, 0.05)';
                        e.currentTarget.style.borderColor = 'rgba(255, 255, 255, 0.1)';
                        e.currentTarget.style.color = '#cbd5e1';
                        e.currentTarget.style.transform = 'translateY(0)';
                        e.currentTarget.style.boxShadow = '0 2px 8px rgba(0, 0, 0, 0.2)';
                      }
                    }}
                  >
                    {/* Animated glow effect for active tab */}
                    {isActive && (
                      <div
                        style={{
                          position: 'absolute',
                          top: 0,
                          left: 0,
                          right: 0,
                          bottom: 0,
                          background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.15), rgba(147, 51, 234, 0.15))',
                          borderRadius: '14px',
                          animation: 'tabGlow 2s ease-in-out infinite',
                          zIndex: -1
                        }}
                      />
                    )}
                    
                    <i 
                      className={item.icon}
                      style={{
                        fontSize: '1rem',
                        color: isActive ? '#ffffff' : '#94a3b8',
                        transition: 'all 0.3s ease'
                      }}
                    ></i>
                    
                    <span style={{ 
                      fontWeight: '700',
                      lineHeight: '1.2',
                      whiteSpace: 'nowrap'
                    }}>
                      {item.label}
                    </span>
                  </NavLink>
                );
              })}
            </div>

            {/* Mobile Menu Toggle */}
            <button
              className="nav__toggle"
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              style={{
                display: 'none',
                background: 'rgba(255, 255, 255, 0.1)',
                border: '1px solid rgba(59, 130, 246, 0.3)',
                borderRadius: '12px',
                flexDirection: 'column',
                cursor: 'pointer',
                padding: '0.75rem',
                gap: '4px',
                transition: 'all 0.3s ease',
                backdropFilter: 'blur(12px)',
                flexShrink: 0
              }}
              onMouseEnter={(e) => {
                e.target.style.background = 'rgba(255, 255, 255, 0.15)';
                e.target.style.borderColor = 'rgba(59, 130, 246, 0.5)';
              }}
              onMouseLeave={(e) => {
                e.target.style.background = 'rgba(255, 255, 255, 0.1)';
                e.target.style.borderColor = 'rgba(59, 130, 246, 0.3)';
              }}
            >
              <span 
                style={{
                  width: '22px',
                  height: '2px',
                  background: 'linear-gradient(135deg, #f1f5f9, #cbd5e1)',
                  borderRadius: '2px',
                  transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                  transform: isMenuOpen ? 'rotate(45deg) translate(5px, 5px)' : 'none'
                }}
              />
              <span 
                style={{
                  width: '22px',
                  height: '2px',
                  background: 'linear-gradient(135deg, #f1f5f9, #cbd5e1)',
                  borderRadius: '2px',
                  transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                  opacity: isMenuOpen ? 0 : 1
                }}
              />
              <span 
                style={{
                  width: '22px',
                  height: '2px',
                  background: 'linear-gradient(135deg, #f1f5f9, #cbd5e1)',
                  borderRadius: '2px',
                  transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                  transform: isMenuOpen ? 'rotate(-45deg) translate(5px, -5px)' : 'none'
                }}
              />
            </button>
          </div>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div 
            className="nav__mobile"
            style={{
              position: 'absolute',
              top: '100%',
              left: 0,
              right: 0,
              background: 'linear-gradient(135deg, rgba(15, 23, 42, 0.98), rgba(30, 41, 59, 0.98))',
              backdropFilter: 'blur(20px)',
              borderTop: '1px solid rgba(148, 163, 184, 0.1)',
              borderBottom: '1px solid rgba(148, 163, 184, 0.1)',
              padding: '1.5rem 0',
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
              animation: 'slideDown 0.3s ease-out'
            }}
          >
            <div className="container">
              <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
                {navItems.map((item) => {
                  const isActive = getCurrentPage() === item.id;
                  return (
                    <NavLink
                      key={item.id}
                      to={item.path}
                      style={{
                        display: 'flex',
                        alignItems: 'center',
                        gap: '1rem',
                        background: isActive 
                          ? 'rgba(96, 165, 250, 0.15)' 
                          : 'transparent',
                        color: isActive ? '#e2e8f0' : '#94a3b8',
                        fontSize: '1.1rem',
                        fontWeight: '600',
                        cursor: 'pointer',
                        padding: '1rem 1.5rem',
                        borderRadius: '12px',
                        textDecoration: 'none',
                        transition: 'all 0.3s ease',
                        border: '2px solid transparent',
                        borderColor: isActive ? 'rgba(96, 165, 250, 0.3)' : 'transparent'
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.background = 'rgba(148, 163, 184, 0.1)';
                        e.target.style.color = '#e2e8f0';
                        e.target.style.borderColor = 'rgba(96, 165, 250, 0.2)';
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.background = isActive ? 'rgba(96, 165, 250, 0.15)' : 'transparent';
                        e.target.style.color = isActive ? '#e2e8f0' : '#94a3b8';
                        e.target.style.borderColor = isActive ? 'rgba(96, 165, 250, 0.3)' : 'transparent';
                      }}
                    >
                      <i className={item.icon} style={{ fontSize: '1.2rem', color: isActive ? '#60a5fa' : '#94a3b8' }}></i>
                      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start' }}>
                        <span style={{ fontWeight: '700' }}>{item.label}</span>
                        <span style={{ fontSize: '0.85rem', opacity: 0.8, color: isActive ? '#cbd5e1' : '#64748b' }}>
                          {item.description}
                        </span>
                      </div>
                    </NavLink>
                  );
                })}
              </div>
            </div>
          </div>
        )}
      </header>

      {/* Custom Styles with Keyframes */}
      <style jsx>{`
        @keyframes pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.7; }
        }

        @keyframes slideDown {
          from {
            opacity: 0;
            transform: translateY(-10px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .tabs-container::-webkit-scrollbar {
          display: none;
        }

        @media (max-width: 1024px) {
          .tabs-container {
            justify-content: flex-start !important;
            padding-bottom: 0.75rem !important;
          }
          
          .tab-item {
            flex-shrink: 0;
            padding: 0.75rem 1rem !important;
            min-width: auto !important;
          }
          
          .tab-item > div {
            gap: 0.25rem !important;
          }
          
          .tab-item span:last-child {
            display: none !important;
          }
        }

        @media (max-width: 768px) {
          .nav__toggle {
            display: flex !important;
          }

          .header-tabs {
            display: none !important;
          }
        }

        @media (max-width: 480px) {
          .tab-item {
            padding: 0.5rem !important;
            gap: 0.5rem !important;
          }
          
          .tab-item span:first-of-type {
            display: none !important;
          }
          
          .tab-item i {
            font-size: 1.25rem !important;
          }
        }

        /* Enhanced focus styles for accessibility */
        .tab-item:focus,
        .nav__toggle:focus {
          outline: 2px solid #60a5fa;
          outline-offset: 2px;
        }

        /* High contrast mode support */
        @media (prefers-contrast: high) {
          .unified-header {
            background: #000 !important;
            border-bottom: 2px solid #fff !important;
          }
          
          .tab-item {
            border: 2px solid #fff !important;
          }
        }

        /* Reduced motion support */
        @media (prefers-reduced-motion: reduce) {
          .tab-item,
          .nav__mobile,
          .nav__toggle span {
            transition: none !important;
            animation: none !important;
          }
        }
      `}</style>
    </>
  );
};

export default Header;
